import react from 'react'
import { useState } from 'react'
import '../css/Auth.css'
import '../styles.css'
import { DomainURL } from '../constants'
import axios from 'axios'
import { Profile } from './profile'
import { BrowserRouter,Route, Routes, useNavigate } from 'react-router-dom'


function Login() {
  const [isCorrectPass, setIsCorrectPass] = useState(true);
  const navigate = useNavigate();

  const handelSubmit = async (e) => {
    e.preventDefault();
    const userId = e.target.loginId.value;
    const password = e.target.password.value;

    try {
      const loginRes = await axios.post(`${DomainURL}/auth/login`, {
        email: userId,
        password: password
      });

      console.log(loginRes.data);

      if (loginRes.data.status === 200) {
        setIsCorrectPass(true);
        navigate('/');
      } else {
        setIsCorrectPass(false);
      }
    } catch (error) {
      console.error('Login Error:', error);
      setIsCorrectPass(false);
    }
  };

  const handleSignup = () => {
    navigate('/register');
  };

  return (
    <div className='flex flex-col items-center justify-center h-screen'>
    <form className='flex flex-col justify-around bg-gray-100 p-8 rounded-lg shadow-lg' onSubmit={handleSubmit}>
      <h2 className='text-2xl font-semibold mb-4'>Login</h2>
      {!isCorrectPass && <div className='text-red-500 mb-4'>Invalid Username/Password</div>}
      <div className='flex flex-col space-y-2'>
        <label htmlFor='loginId' className='text-sm font-medium'>Username or Email</label>
        <input
          type='text'
          id='loginId'
          name='loginId'
          className='border border-gray-300 rounded-md py-2 px-3 focus:outline-none focus:ring-2 focus:ring-blue-500'
          required
        />
        <label htmlFor='password' className='text-sm font-medium'>Password</label>
        <input
          type='password'
          id='password'
          name='password'
          className='border border-gray-300 rounded-md py-2 px-3 focus:outline-none focus:ring-2 focus:ring-blue-500'
          required
        />
      </div>
      <div className='flex justify-between items-center mt-4'>
        <button
          type='submit'
          className='bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500'
        >
          Login
        </button>
        <button
          type='button'
          className='text-blue-500 hover:text-blue-600 focus:outline-none'
          onClick={handleSignup}
        >
          Signup
        </button>
      </div>
    </form>
  </div>
  
  );
}

function Register(){
  const handleRegister=async (e)=>{
    e.preventDefault()
    const name = e.target.form[0].value
    const username = e.target.form[1].value
    const email = e.target.form[2].value
    const phone = e.target.form[3].value
    const password = e.target.form[4].value
console.log(name,username,email,phone,password)
const registerRes = await axios.post(`${DomainURL}/auth/register`,{
  data:{
    name,
    username,
    email,
    phone,
    password,

  }
  
})
console.log(registerRes)
    
  }

  return (
    <div className='flex items-center justify-center h-screen'>
  <form className='bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 w-full max-w-md'>
    <h2 className='text-3xl mb-6 text-center font-bold text-gray-800'>Signup</h2>
    <div className='mb-4'>
      <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='name'>Name</label>
      <input
        className=' input '
        id='name'
        type='text'
        placeholder='Enter your name'
        required
      />
    </div>
    <div className='mb-4'>
      <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='username'>Username</label>
      <input
  className='input'
  id='username'
  type='text'
  placeholder='Choose a username'
  required
/>


    </div>
    <div className='mb-4'>
      <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='email'>Email</label>
      <input
        className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
        id='email'
        type='email'
        placeholder='Enter your email'
        required
      />
    </div>
    <div className='mb-4'>
      <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='phone'>Phone</label>
      <input
        className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
        id='phone'
        type='text'
        placeholder='Enter your phone number'
        required
      />
    </div>
    <div className='mb-6'>
      <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='password'>Password</label>
      <input
        className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
        id='password'
        type='password'
        placeholder='Enter your password'
        required
      />
    </div>
    <div className='flex flex-col items-center justify-center'>
      <button
        className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline'
        type='submit'
        onClick={handleRegister}
      >
        Signup
      </button>
      <p className='mt-4'>Already have an account?   <a className='text-blue-600' href='/login' >Login</a></p>
    </div>
    
  </form>
</div>


  )
}

function Logout(){
  const navigate = useNavigate();
const handleLogout=async(e)=>{
  
  e.preventDefault()
  try {
  const res = await axios.post(`${DomainURL}/auth/logout`)
  console.log(res)
  }
  catch(err){
    console.log(err)
  }
  navigate('/login')
}

  return(
    <div className="flex flex-col items-center justify-center h-screen">
    <div >
      {/* This empty div helps to vertically center the content */}
      
      <div className="flex flex-col items-center justify-center">
        <h2 className="text-3xl font-bold mb-8 text-center text-gray-800">You have successfully logged out</h2>
        <button onClick={handleLogout} className="px-4 py-2 bg-blue-500 text-white rounded-md shadow-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 transition-colors duration-300">
          Login
        </button>
      </div>
      <div className="h-1/5"></div>
    </div>
  </div>
  
  
  )
}

export {Login, Register,Logout}
