import { FaComments, FaHeart,FaShare} from "react-icons/fa";
import React,{useState,useEffect} from "react";
import axios from "axios";
import { DomainURL } from "../constants";




function TweetsPage() {
  const [tweets, setTweets] = useState([]); // State to hold the tweets
  var [userDetails,setUserDetails]=useState({})
  const [loading, setLoading] = useState(true); // State to handle loading state
  const [error, setError] = useState(null); // State to handle errors
  const [openCommentBoxId, setOpenCommentBoxId] = useState(null)
  useEffect(() => {
    // Define an async function to fetch tweets
    const fetchTweets = async () => {
      try {
        const res = await getAllTweets();
        console.log('tweets',res)
        let userRes = await getAllUserOfTweets(res)
        console.log('tweetsUser',userRes)
        setTweets(res); // Assuming res is the array of tweets
        setUserDetails(userRes)
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTweets(); // Call the async function
  }, []); // Empty dependency array means this effect runs once on mount

  const handelCommentBoxToggle=(id)=>{
    setOpenCommentBoxId(prevId => (prevId==id ? null : id))
  }
  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <>
      <div className="tweet list ml-8 w-2/3">
        {/* Render the list of tweets */}
        {tweets?.length === 0 ? (
          <p>No tweets available.</p>
        ) : (
          <ul>
            {tweets?.map(tweet => (
              <Tweet key={tweet._id} tweet={tweet} user={userDetails[tweet.userId]} isCommentBoxOpen={openCommentBoxId==tweet._id} onCommentBoxToggle={()=>handelCommentBoxToggle(tweet._id)}></Tweet> // Render each tweet
            ))}
          </ul>
        )}
      </div>
      <div id="tweet-create" className="fixed bottom-0 left-0 w-full bg-white border-t border-gray-200 p-4 shadow-lg">
        <input
          type="text"
          placeholder="Title"
          className="w-full border border-gray-400 rounded-md my-1 mx-1 px-1 text-sm"
        />
        
        <div className="flex items-start gap-2 p-1">
          <textarea
            placeholder="What’s on your mind?"
            className="border border-gray-300 rounded-lg p-2 text-sm resize-none focus:outline-none focus:border-blue-400 transition duration-150 ease-in-out flex-grow"
          ></textarea>
          <button
            type="submit"
            className="bg-blue-500 text-white rounded-lg px-3 py-1 text-sm hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-300 transition duration-150 ease-in-out"
          >
            Create Tweet
          </button>
        </div>

      </div>
  
    </>
  );
}




function Tweet(props) {
  const { user, tweet, isCommentBoxOpen, onCommentBoxToggle} = props;
  const [likeCount,setLikeCount]=useState(0)

  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden border border-gray-200 mb-2">
      <div className="flex items-start p-2 border-b border-gray-200">
        <img 
          src={user?.avatarUrl}
          alt={user?.username}
          className="h-8 w-8 rounded-full object-cover mr-4"
        />
        <div className="flex-1">
          <p className="font-semibold text-sm text-gray-800">{user?.username}</p>
          <p className="text-gray-600 text-xs p-0">{user?.name}</p>
        </div>
        <p className="text-xs   mb=0 text-gray-400 ">{tweet?.creationDateTime?.slice(0,21)}</p>
      </div>
      <div className="py-1 px-4 border-b border-gray-200">
        <p className="text-base font-semibold text-gray-900">{tweet?.title}</p>
        <pre className="my-2 text-xs text-gray-700 whitespace-pre-wrap">{tweet?.bodyText}</pre>
      </div>
      <div className="flex justify-around   border-gray-200 bg-gray-50">
        <button onClick={()=>{setLikeCount(likeCount+1)}} className="flex text-sm items-center text-gray-500 hover:text-red-500">
          <FaHeart className="mr-1" />
           {likeCount}
        </button>
        <button onClick={onCommentBoxToggle}className="flex text-sm items-center text-gray-500 hover:text-blue-500">
          <FaComments className="mr-1" />
          Comment
        </button>
        <button className="flex items-center text-sm text-gray-500 hover:text-green-500">
          <FaShare className="mr-1" />
          Share
        </button>
      </div>
      {isCommentBoxOpen && 
        <div className=" p-1 flex items-center">
          <textarea
          className="flex-grow border text-sm border-gray-300 rounded-md p-1 mx-2 resize-y overflow-auto min-h-8"
          placeholder="Write a comment..."
            rows="1"  
          ></textarea>
          <button className="border text-xs border-gray-500 rounded-md p-1">
            Comment
          </button>
        </div>
      }
    </div>
  );
}

export default TweetsPage;

//all util functions
const getAllTweets=async ()=>{
  try{
    const res = await axios.get(`${DomainURL}/tweets/get-all-tweets`)
    
    return res.data.data

  }catch(err){
    return err
  }

}

//need to improve
const getAllUserOfTweets=async (tweets)=>{
  let tweetsUserIds = new Set()
  
  tweets.forEach((tweet)=>{
    tweetsUserIds.add(tweet.userId)
  })
  let userDetails={}
  try{
    let res = await axios.get(`${DomainURL}/auth/all-users`)
    res=res.data.data[0].data
    res.forEach((ele)=>{
      if(tweetsUserIds.has(ele._id)){
        userDetails[`${ele._id}`]=ele
      }
    })
    return userDetails
  }
  catch(err){
    return err
  }
}

export {TweetsPage}
