import React,{useEffect,useState,useContext} from "react";

import axios from "axios";
import { DomainURL } from "../constants";
import { useLocation } from "react-router-dom";
import { localStorage } from "../App";

const Profile= function(props){
  const [activeTab, setActiveTab] = useState('followers');
  const [list,setList] = useState({following:[], follower:[],all:[]})
  const [state, setState] = useContext(localStorage)

let {myDetails}=location.state || {}
let userList


const handleTabClick = async (tab) => {
  
  if(tab=='following' ){
    const res= await axios(`${DomainURL}/follow/following-list/${myDetails._id}/0`)
    setList((ps)=>({...ps, following:res.data.data }))
  }
  else if(tab=='follower' ){
    const res= await axios(`${DomainURL}/follow/follower-list/${myDetails._id}/0`)
    setList((ps)=>({...ps, follower:res.data.data }))
  }
  else if(tab=='all' && list.all.length == 0){ 
    const res= await axios(`${DomainURL}/auth/all-users`)
    setList((ps)=>({...ps, all:res.data.data[0].data}))
   
  }
  setActiveTab(tab);
};

const returnList=()=>{
  if(activeTab == 'all') return list.all
  else if( activeTab == 'following') return list.following
  else return list.follower
}
  return(
  <div className="ml-16 w-1/2">
    <div className="flex border border-gray-200 px-8 py-12  rounded-md shadow-sm">
      <div id="profile" className="flex ml-4 w-1/3 items-center">
        <img
          src={myDetails?.avatarUrl}
          alt={myDetails.username}
          className="h-12 w-16 rounded-full object-cover mr-8"
        />
      </div>
      <div className="flex flex-grow justify-around">
        <div id="my-tweets" className="flex flex-col items-center">
          <p className="text-center">{myDetails.tweets}</p>
          <p className="text-center">Tweets</p>
        </div>
        <div id="follower" className="flex flex-col items-center ">
          <p className="text-center">{myDetails.follower}</p>
          <p className="text-center">Followers</p>
        </div>
        <div id="following" className="flex flex-col items-center">
          <p className="text-center">{myDetails.following}</p>
          <p className="text-center">Following</p>
        </div>
      </div>
      </div>
      <div>
      <div>
        <div className="flex justify-around">
          <a onClick={() => handleTabClick('my-tweets')} className={`border text-center border-gray-200 w-full  ${activeTab === 'my-tweets' ? 'bg-blue-500 text-white' : ''}`} >
            My Tweets
          </a>
          <a onClick={() => handleTabClick('followers')}  className={`border text-center border-gray-200 w-full  ${activeTab === 'followers' ? 'bg-blue-500 text-white' : ''}`}  >
            Followers
          </a>
          <a onClick={() => handleTabClick('following')}  className={`border text-center border-gray-200 w-full ${activeTab === 'following' ? 'bg-blue-500 text-white' : ''}`}  >
            Following
          </a>
          <a onClick={() => handleTabClick('all')} className={`border text-center border-gray-200 w-full  ${activeTab === 'all' ? 'bg-blue-500 text-white' : ''}`} >
            Suggestions
          </a>
        </div>
        <ul id="list">
          <UserItem  userList={returnList()} activeTab={activeTab} myDetails={myDetails}></UserItem>
        </ul>
      </div>

    </div>
  </div>
  )
}

const UserItem = function (props) {
  const { userList, activeTab, myDetails } = props;
  const [followedUsers, setFollowedUsers] = useState(new Set());

  const addFollow = (userId) => {
    setFollowedUsers(prevState => new Set(prevState).add(userId));
  };
  const handleAction = async(e, userId) => {
    e.stopPropagation(); // Prevents event from bubbling up
    if (activeTab === 'all') {
      const res= await followUser(myDetails._id,userId)
      if(res.status == 200){
        addFollow(userId)
      }
      
    } else {
      console.log('Send message to user with ID:', userId);
    }
  };

  return (
    <ul>
      {userList?.map(user => (
        <li
          key={user._id}
          className="flex container items-center border border-gray-200 p-4 rounded-md shadow-sm mb-1"
         
        >
          <img
            src={user.avatarUrl}
            alt={user.username}
            className="h-12 w-12 rounded-full object-cover mr-4"
          />
          <div className='flex-1'>
            <h2 className="text-lg pb-0 mb-0 font-semibold text-gray-800">{user.username}</h2>
            <p className="text-xs text-gray-500">{user.name}</p>
          </div>
          <button className="border border-black-500 shadow-md bg-grey-500 rounded-md px-4 py-1"
            onClick={(e) => handleAction(e, user._id)} // Pa4ss the user ID and event to the handler
          >
            {activeTab === 'all' ? followedUsers.has(user._id) ? 'Following': 'Follow' : 'Message'}
          </button>
        </li>
      ))}
    </ul>
  );
};

async function followUser  (myId,followingId){
console.log('followUser',myId,followingId)
try{
const res = await axios.post(`${DomainURL}/follow/follow-user`,{
  followingUserId:followingId,
  myUserId:myId
})
return res
}
catch(err){
  return err
}
}

export {Profile}
