import React,{useEffect,useState} from "react";

import axios from "axios";
import { DomainURL } from "../constants";
import { useLocation } from "react-router-dom";

const Profile= function(props){
  const [activeTab, setActiveTab] = useState('followers');
  const [list,setList] = useState({following:[], follower:[],all:[]})
  const location = useLocation()

let {myDetails}=location.state || {}
let userList
console.log('propsProfile',props,location)

const handleTabClick = async (tab) => {
  console.log(tab,myDetails)
  if(tab=='following' && list.following.length == 0){
    const res= await axios(`${DomainURL}/follow/following-list/${myDetails._id}/0`)
    console.log("ffolloqwq",res)
  }
  else if(tab=='follower' && list.follower.length == 0){
    const res= await axios(`${DomainURL}/follow/follower-list/${myDetails._id}/0`)
    console.log("ffolloqwq",res)
  }else if(tab=='all' && list.all.length == 0){ 
    const res= await axios(`${DomainURL}/auth/all-users`)
    setList((prevstate)=>({
      ...prevstate,all:res.data.data[0].data
    }))
    console.log("ffolloqwq",res)
  }
  setActiveTab(tab);
};

const renderList = () => {
  if (activeTab === 'followers') {
    return userList.map(user => <UserItem key={user._id} userDetails={user} activeTab={activeTab} />);
  } else if (activeTab === 'following'){
    return userList.map(user => <UserItem key={user._id} userDetails={user} activeTab={activeTab}/>);
  }  else{
    return list.all.map(user => <UserItem key={user._id} userDetails={user} activeTab={activeTab}/>);
  }
};
userList=[{
  username:'a.lover',
  name:'akshay neje'

},
{
  username:'onkar',
  name:'onkar kadam'
},
{
  username:'praju',
  name:'prajakta mali'

}]
returnList=()=>{
  if(activeTab == 'all') return list.all
  else if( activeTab = 'following') return list.following
  else return list.follower
}
  return(
  <div className="ml-16 w-1/2">
    <div className="flex border border-gray-200 px-8 py-12  rounded-md shadow-sm">
      <div id="profile" className="flex ml-4 w-1/3 items-center">
        <img
          src={myDetails.avatarUrl}
          alt={myDetails.username}
          className="h-12 w-16 rounded-full object-cover mr-8"
        />
      </div>
      <div className="flex flex-grow justify-around">
        <div id="my-tweets" className="flex flex-col items-center">
          <p className="text-center">{myDetails.tweets}</p>
          <p className="text-center">Tweets</p>
        </div>
        <div id="follower" className="flex flex-col items-center ">
          <p className="text-center">{myDetails.follower}</p>
          <p className="text-center">Followers</p>
        </div>
        <div id="following" className="flex flex-col items-center">
          <p className="text-center">{myDetails.following}</p>
          <p className="text-center">Following</p>
        </div>
      </div>
      </div>
      <div>
      <div>
        <div className="flex justify-around">
          <a 
            className={`border text-center border-gray-200 w-full  ${activeTab === 'all' ? 'bg-blue-500 text-white' : ''}`} 
            onClick={() => handleTabClick('all')}
          >
            All
          </a>
          <a 
            className={`border text-center border-gray-200 w-full  ${activeTab === 'followers' ? 'bg-blue-500 text-white' : ''}`} 
            onClick={() => handleTabClick('followers')}
          >
            Followers
          </a>
          <a 
            className={`border text-center border-gray-200 w-full ${activeTab === 'following' ? 'bg-blue-500 text-white' : ''}`} 
            onClick={() => handleTabClick('following')}
          >
            Following
          </a>
        </div>
        <ul id="list">
          <UserItem  userDetails={returnList()} ></UserItem>
        </ul>
      </div>

    </div>
  </div>
  )
}

const UserItem = function (props){
  const {key,userDetails,activeTab} =props
  console.log(props)
  const handelAction=(e)=>{
    if(activeTab == 'all'){

    }
    else{

    }
    console.log('send message',e.target)
  }
 
  return (
    <li key={key} onClick={(e)=>console.log('jgff',e)} className="flex container items-center border border-gray-200 p-4 rounded-md shadow-sm mb-1">
      <img
        src={userDetails.avatarUrl}
        alt={userDetails.username}
        className="h-12 w-12 rounded-full object-cover mr-4"
      />
      <div className='flex-1'>
        <h2 className="text-lg pb-0 mb-0 font-semibold text-gray-800">{userDetails.username}</h2>
        <p className="text-xs   text-gray-500">{userDetails.name}</p>
      </div>
      
      <button className='' onClick={handelAction}>{activeTab == 'all' ? 'Follow' : 'Message'}</button>
      
    </li>

  );
};

export {Profile}
