import React,{useEffect,useState} from "react";
import {UserItem} from './Follow'
import axios from "axios";
import { DomainURL } from "../constants";
import { useLocation } from "react-router-dom";

const Profile= function(props){
  const [activeTab, setActiveTab] = useState('followers');
  const [list,setList] = useState({following:[], follower:[]})
  const location = useLocation()

let {myDetails}=location.state || {}
let userList
console.log('propsProfile',props,location)
const handleTabClick = async (tab) => {
  console.log(tab,myDetails)
  if(tab=='following' && list.following.length == 0){
    const res= await axios(`${DomainURL}/follow/following-list/${myDetails.username}/0`)
  }
  setActiveTab(tab);
};
useEffect(()=>{
   //const res = axios('')
},[])
const renderList = () => {
  if (activeTab === 'followers') {
    return userList.map(user => <UserItem key={user.id} userDetails={user} />);
  } else {
    return userList.map(user => <UserItem key={user.id} userDetails={user} />);
  }
};
userList=[{
  username:'a.lover',
  name:'akshay neje'

},
{
  username:'onkar',
  name:'onkar kadam'
},
{
  username:'praju',
  name:'prajakta mali'

}]
// myDetails={
//   username:'me',
//   tweets:0,
//   follower:0,
//   following:0

// }
  return(
  <div className="ml-16 w-1/2">
    <div className="flex border border-gray-200 px-8 py-12  rounded-md shadow-sm">
      <div id="profile" className="flex ml-4 w-1/3 items-center">
        <img
          src={myDetails.avatarUrl}
          alt={myDetails.username}
          className="h-12 w-16 rounded-full object-cover mr-8"
        />
      </div>
      <div className="flex flex-grow justify-around">
        <div id="my-tweets" className="flex flex-col items-center">
          <p className="text-center">{myDetails.tweets}</p>
          <p className="text-center">Tweets</p>
        </div>
        <div id="follower" className="flex flex-col items-center ">
          <p className="text-center">{myDetails.follower}</p>
          <p className="text-center">Followers</p>
        </div>
        <div id="following" className="flex flex-col items-center">
          <p className="text-center">{myDetails.following}</p>
          <p className="text-center">Following</p>
        </div>
      </div>
      </div>
      <div>
      <div>
        <div className="flex justify-around">
          <a 
            className={`border border-gray-200 w-full  ${activeTab === 'followers' ? 'bg-blue-500 text-white' : ''}`} 
            onClick={() => handleTabClick('followers')}
          >
            Followers
          </a>
          <a 
            className={`border border-gray-200 w-full ${activeTab === 'following' ? 'bg-blue-500 text-white' : ''}`} 
            onClick={() => handleTabClick('following')}
          >
            Following
          </a>
        </div>
        <div id="list">
          {renderList()}
        </div>
      </div>

    </div>
  </div>
  )
}

export {Profile}
