import { FaComments, FaHeart,FaShare} from "react-icons/fa";
import React,{useState,useEffect} from "react";
import axios from "axios";
import { DomainURL } from "../constants";




function TweetsPage() {
  const [tweets, setTweets] = useState([]); // State to hold the tweets
  const [loading, setLoading] = useState(true); // State to handle loading state
  const [error, setError] = useState(null); // State to handle errors

  useEffect(() => {
    // Define an async function to fetch tweets
    const fetchTweets = async () => {
      try {
        const res = await getAllTweets();
        console.log('tweets',res)
        setTweets(res); // Assuming res is the array of tweets
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTweets(); // Call the async function
  }, []); // Empty dependency array means this effect runs once on mount

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <>
      <div className="tweet list">
        {/* Render the list of tweets */}
        {tweets?.length === 0 ? (
          <p>No tweets available.</p>
        ) : (
          <ul>
            {tweets?.map(tweet => (
              <Tweet key={tweet.id}>{tweet}</Tweet> // Render each tweet
            ))}
          </ul>
        )}
      </div>
      <div className="create tweet">
        {/* Your form or component for creating new tweets */}
      </div>
    </>
  );
}

export default TweetsPage;


function Tweet(prop){
  const{ user,tweet}=prop
  return (
    <>
      <div id="tweet-profile">
      <img
          src={user?.avatarUrl}
          alt={user?.username}
          className="h-12 w-16 rounded-full object-cover mr-8"
        />
        <div>
          <p>{user?.username}</p>
          <p>{user?.name} </p>
        </div>
        <div>
          <p>{tweet?.creationDateTime}</p>
        </div>
      </div>
      <div id="tweet-body">
        <p>{tweet.title}</p>
        <pre>
          {tweet.body}
        </pre>

      </div>
      <div id="tweet-bott-bar">
      <FaHeart></FaHeart>
      <FaComments></FaComments>
      <FaShare></FaShare>
      

      </div>
      <div id="tweet-comment">

      </div>
    
    
    </>
  )
}

const getAllTweets=async ()=>{
  try{
    const res = await axios.get(`${DomainURL}/tweets/get-all-tweets`)
    
    return res.data.data

  }catch(err){
    return err
  }

}

export {TweetsPage}
