import React from 'react';

const Follower = ({ follower }) => {
  follower={
    username:'a.lover',
    name:'akshay neje'

  }
  return (
    <div className="flex items-center border border-gray-200 p-4 rounded-md shadow-sm mb-4">
      <img
        src={follower.avatarUrl}
        alt={follower.username}
        className="h-12 w-12 rounded-full object-cover mr-4"
      />
      <div>
        <h2 className="text-lg mb-0 font-semibold text-gray-800">{follower.username}</h2>
        <p className="text-xsm mt-0 text-gray-600">{follower.name}</p>
      </div>
      <div>
        <button className=' m-30 color-blue-400 '>Follow</button>
      </div>
    </div>
  );
};

export  {Follower};
