import React from 'react';

const UserItem = function (props){
  const {userDetails} =props
  return (
    <div className="flex items-center w-30% border border-gray-200 p-4 rounded-md shadow-sm mb-4">
      <img
        src={userDetails.avatarUrl}
        alt={userDetails.username}
        className="h-12 w-12 rounded-full object-cover mr-4"
      />
      <div>
        <h2 className="text-lg pb-0 font-semibold text-gray-800">{userDetails.username}</h2>
        <p className="text-xsm mt-0 h-4 text-gray-600">{userDetails.name}</p>
      </div>
      <div>
        <button className=' m-30 color-blue-400 '>Message</button>
      </div>
    </div>
  );
};



export  {UserItem};
