import { FaComments, FaHeart,FaShare} from "react-icons/fa";
import React,{useState,useEffect} from "react";
import axios from "axios";
import { DomainURL } from "../constants";




function TweetsPage() {
  const [tweets, setTweets] = useState([]); // State to hold the tweets
  var [userDetails,setUserDetails]=useState({})
  const [loading, setLoading] = useState(true); // State to handle loading state
  const [error, setError] = useState(null); // State to handle errors
  const [openCommentBoxId, setOpenCommentBoxId] = useState(null)
  useEffect(() => {
    // Define an async function to fetch tweets
    const fetchTweets = async () => {
      try {
        const res = await getAllTweets();
        console.log('tweets',res)
        const userRes = await getAllUserOfTweets(res)
        setTweets(res); // Assuming res is the array of tweets
        setUserDetails(userRes)
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTweets(); // Call the async function
  }, []); // Empty dependency array means this effect runs once on mount

  const handelCommentBoxToggle=(id)=>{
    setOpenCommentBoxId(prevId => (prevId==id ? null : id))
  }
  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <>
      <div className="tweet list ml-8 w-2/3">
        {/* Render the list of tweets */}
        {tweets?.length === 0 ? (
          <p>No tweets available.</p>
        ) : (
          <ul>
            {tweets?.map(tweet => (
              <Tweet key={tweet._id} tweet={tweet} user={userDetails[tweet._id]} isCommentBoxOpen={openCommentBoxId==tweet._id} onCommentBoxToggle={()=>handelCommentBoxToggle(tweet._id)}></Tweet> // Render each tweet
            ))}
          </ul>
        )}
      </div>
      <div id="tweet-create"className=" fixed">hello
        {/* Your form or component for creating new tweets */}
      </div>
    </>
  );
}




function Tweet(props) {
  const { user, tweet, isCommentBoxOpen, onCommentBoxToggle} = props;
  const [likeCount,setLikeCount]=useState(0)
  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden border border-gray-200 mb-2">
      <div className="flex items-start p-2 border-b border-gray-200">
        <img 
          src={user?.avatarUrl}
          alt={user?.username}
          className="h-8 w-8 rounded-full object-cover mr-4"
        />
        <div className="flex-1">
          <p className="font-semibold text-lg text-gray-800">{user?.username}</p>
          <p className="text-gray-600">{user?.name}</p>
          <p className="text-xs text-gray-400 mt-1">{tweet?.creationDateTime}</p>
        </div>
      </div>
      <div className="p-4 border-b border-gray-200">
        <p className="text-xl font-semibold text-gray-900">{tweet?.title}</p>
        <pre className="mt-2 text-gray-700 whitespace-pre-wrap">{tweet?.bodyText}</pre>
      </div>
      <div className="flex justify-around p-2 border-t border-gray-200 bg-gray-50">
        <button onClick={()=>{setLikeCount(likeCount+1)}} className="flex items-center text-gray-500 hover:text-red-500">
          <FaHeart className="mr-1" />
           {likeCount}
        </button>
        <button onClick={onCommentBoxToggle}className="flex items-center text-gray-500 hover:text-blue-500">
          <FaComments className="mr-1" />
          Comment
        </button>
        <button className="flex items-center text-gray-500 hover:text-green-500">
          <FaShare className="mr-1" />
          Share
        </button>
      </div>
      {isCommentBoxOpen && 
     <div className="p-4 flex items-center">
     <input
       type="text"
       className="flex-grow border border-gray-300 rounded-md p-2 mr-2"
       placeholder="Write a comment..."
     />
     <button className="border border-gray-500 rounded-md p-2">
       Comment
     </button>
   </div>
   
      }
    </div>
  );
}

export default TweetsPage;

//all util functions
const getAllTweets=async ()=>{
  try{
    const res = await axios.get(`${DomainURL}/tweets/get-all-tweets`)
    
    return res.data.data

  }catch(err){
    return err
  }

}

//need to improve
const getAllUserOfTweets=async (tweets)=>{
  const tweetsUserIds = new Set()
  
  tweets.forEach((ele)=>{
    tweetsUserIds.add(ele._id)
  })
  const userDetails={}
  try{
    const res = await axios.get(`${DomainURL}/auth/all-users`)
    res=res.data.data
    res.foreach((ele)=>{
      if(tweetsUserIds.has(ele._id)){
        userDetails[`${ele._id}`]=ele
      }
    })
    return userDetails
  }
  catch(err){
    return err
  }
}

export {TweetsPage}
